import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Form from './components/Form';
import Result from './components/Result';

const API_KEY = 'daf26c99ddf68a77d25d0c92137da0e5';

interface WeatherData {
  temperature: number;
  feels_like: number;
  wind_speed: number;
  description: string;
  city: string;
}

const App: React.FC = () => {
  const [weatherData, setWeatherData] = useState<WeatherData | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchWeather = async (city: string) => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(
              city
          )}&units=imperial&appid=${API_KEY}`
      );
      if (response.ok) {
        const data = await response.json();
        const { main, weather, wind, name } = data;
        const temperature = main.temp;
        const feels_like = main.feels_like;
        const wind_speed = wind.speed;
        const description = weather[0].description;
        setWeatherData({ temperature, feels_like, wind_speed, description, city: name });
      } else {
        setError(`Error fetching weather: ${response.statusText}`);
      }
    } catch (error: any) {
      console.error('Error fetching weather:', error);
      setError(`Error fetching weather: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="App">
        <Header title="Weather App" />
        <Form onSearch={fetchWeather} />
        <Result weatherData={weatherData} loading={loading} error={error} />
      </div>
  );
};

export default App;
